    fun generateFromString(string: String, words: Int): String {
        val hash = string.hashCode().toLong()
        val uniqueLong = abs(hash % 10000)
        val random = Random(uniqueLong)
        val randomShuffle = randomText
            .shuffled(random)
        return randomShuffle
            .take(words)
            .joinToString(" ")
            .split(". ").joinToString(". ") {
                it.capital()
            }
            .capital()
            .trimEnd(',', '.', ' ')
            .plus(".")
    }

    private fun String.capital(): String {
        return replaceFirstChar { if (it.isLowerCase()) it.titlecase(Locale.getDefault()) else it.toString() }
    }
